# Creating the htnl code to generate the options

def gen_options_string(list_of_tuples): # example [(country1, html-value1), (country2, html-value2),...]
    string = ""
    for tuple in list_of_tuples:
        string += f'<option value = "{tuple[0]}"> {tuple[1].title()} </option>'
    return string

countries_options, lang_options = gen_options_string(countries), gen_options_string(languages)

print(countries_options)
print(lang_options)